
public class GanzzahlOperationen {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=10;
System.out.println(a++);
System.out.println(a);
 long langzahl = 1234567890123L;
 int kurz = 123;
System.out.println(langzahl%kurz);
System.out.println(1234&123);
double d1 = 123456.789;
double d2 = 123.456;
System.out.println(d1%d2);
	}

}
